# Given an array of integers arr of even length n and an integer k.
# We want to divide the array into exactly n / 2 pairs such that the sum of each pair is divisible by k.
# Return true If you can find a way to do that or false otherwise.
# @param {Integer[]} arr
# @param {Integer} k
# @return {Boolean}
def can_arrange(arr, k)
  return false if (arr.sum % k) > 0
  comparison_array = arr
  counter = 0
  arr.each_index do |index|
    comparison_index = 0
    until comparison_index == comparison_array.length || ((arr[index] + comparison_array[comparison_index]) % k) == 0
      counter += 1
      comparison_index += 1
    end
    comparison_array.delete(comparison_array[index])
    comparison_array.delete(comparison_array[comparison_index])
    comparison_index = 0
  end
  counter >= arr.length / 2 ? true : false
  # counter
end

